"use strict";
class Calculator {
    constructor(a, b) {
        this.a = a;
        this.b = b;
    }
    add() {
        return this.a + this.b;
    }
    subtract() {
        return this.a - this.b;
    }
    multiply() {
        return this.a * this.b;
    }
    divide() {
        if (this.a === 0) {
            throw new Error("You can't divide by zero");
        }
        return this.a / this.b;
    }
}
