export class Calculator {

    constructor( public a: number, public b: number) { }

    add(): number {
        return this.a + this.b;
    }

    subtract(): number {
        return this.a - this.b;
    }

    multiply(): number {
        return this.a * this.b;
    }

    divide(): number {
        if (this.a === 0) {
            throw new Error("You can't divide by zero");
          }
        return this.a / this.b;
    }
}


